const STATE = Object.freeze({
  IDLE: "idle",
  FETCHING: "fetching",
  ERROR: "error",
});
const ACTIONS = Object.freeze({
  FETCH_LISTINGS: "FETCH_LISTINGS",
  FETCH_LISTINGS_SUCCESS: "FETCH_LISTINGS_SUCCESS",
  FETCH_LISTINGS_ERROR: "FETCH_LISTINGS_ERROR",
  VIEW_LISTING: "VIEW_LISTING",
  REPLY: "REPLY",
});

//Initial State and Actions
const initialState = {
  listings: [], // array of listing object,
  state: STATE.IDLE, // enum state of page/slice "idle" | "fetching" | "error",
  error: undefined, // error message
};

const listingsReducer = (state, action) => {
  switch (action.type) {
    case ACTIONS.FETCH_LISTINGS:
      return {
        ...state,
        state: STATE.FETCHING,
        listings: [],
      };
    case ACTIONS.FETCH_LISTINGS_SUCCESS:
      return {
        ...state,
        state: STATE.IDLE,
        listings: [...action.listings],
      };
    case ACTIONS.FETCH_LISTINGS_ERROR:
      return {
        ...state,
        state: STATE.ERROR,
        listings: [],
        error: action.error,
      };

    case ACTIONS.VIEW_LISTING:
    case ACTIONS.REPLY:
    default:
      return state;
  }
};

const ListingsContext = React.createContext();

const ListingsProvider = ({ children }) => {
  const [state, dispatch] = React.useReducer(listingsReducer, initialState);

  const value = {
    listings: state.listings,
    state: state.state,
    error: state.error,
    fetch: (endpoint, query) => {
      dispatch({ type: ACTIONS.FETCH_LISTINGS });
      fetch(endpoint)
        .then((response) => {
          if (response.ok) {
            return response.json();
          } else {
            throw new Error("Something went wrong");
          }
        })
        .then((response) => {
          dispatch({
            type: ACTIONS.FETCH_LISTINGS_SUCCESS,
            listings: response,
          });
        })
        .catch((error) => {
          dispatch({
            type: ACTIONS.FETCH_LISTINGS_SUCCESS,
            error: error.message,
          });
        });
    },
  };

  return (
    <ListingsContext.Provider value={value}>
      {children}
    </ListingsContext.Provider>
  );
};

const formatAsCurrency = (int) => {
  // TODO
};

const Listing = (props) => {
  // TODO
  // This should be the component which renders an individual listing to the page

  return <div className="listing"></div>;
};

const ListingsContainer = ({ dataEndpoint, keyword = "", location }) => {
  const context = React.useContext(ListingsContext);

  React.useEffect(() => {
    context.fetch(dataEndpoint, keyword);
  }, [dataEndpoint, keyword]);

  return (
    <div>
      {context.state}
      {context.error}
      {JSON.stringify(context.listings)}
      <div className="listings__header"></div>
      <div className="listings__grid"></div>
    </div>
  );
};

const Listings = (props) => (
  <ListingsProvider>
    <ListingsContainer {...props} />
  </ListingsProvider>
);

export default Listings;
