const STATE = Object.freeze({
  IDLE: "idle",
  FETCHING: "fetching",
  ERROR: "error",
});
const ACTIONS = Object.freeze({
  FETCH_LISTINGS: "FETCH_LISTINGS",
  LISTINGS_FETCH_SUCCESS: "LISTINGS_FETCH_SUCCESS",
  LISTINGS_FETCH_ERROR: "LISTINGS_FETCH_ERROR",
  VIEW_LISTING: "VIEW_LISTING",
  REPLY: "REPLY",
});

//Initial State and Actions
const initialState = {
  listings: [], // array of listing object,
  state: STATE.IDLE, // enum state of page/slice "idle" | "fetching" | "error",
  query: "", // string; search term
  error: undefined, // error message
};

const listingsReducer = (state, action) => {
  switch (action.type) {
    case ACTIONS.FETCH_LISTINGS:
      return {
        ...state,
        state: STATE.FETCHING,
        listings: [],
      };
    case ACTIONS.LISTINGS_FETCH_SUCCESS:
      return {
        ...state,
        state: STATE.IDLE,
        listings: [...action.listings],
      };
    case ACTIONS.LISTINGS_FETCH_ERROR:
      return {
        ...state,
        state: STATE.ERROR,
        listings: [],
        error: action.error,
      };

    case ACTIONS.VIEW_LISTING:
    case ACTIONS.REPLY:
    default:
      return state;
  }
};

const ListingsContext = React.createContext();

const ListingsProvider = ({ children }) => {
  const [state, dispatch] = React.useReducer(listingsReducer, initialState);

  const value = {
    listings: state.listings,
    state: state.state,
    error: state.error,
    fetch: (query) => {
      dispatch({ type: ACTIONS.FETCH_LISTINGS, query });
    },
  };

  return (
    <ListingsContext.Provider value={value}>
      {children}
    </ListingsContext.Provider>
  );
};

